        function randbyweight(arr) {
            var rand = 0;
            for (var i in arr) {
                rand +=arr[i];
            }
            rand = Math.floor((Math.random() * rand) + 1);
            for (var i in arr) {
                rand -=arr[i];
                if (rand <= 0) {
                    return i;
                }
            }
        }

