        var ads_loader_special = function(cl, html, script = null, callback = null, position = 0){
            var is_first_load_page  = document.querySelector('#check_is_first_load').dataset.type;
            var event_is_load = false;
            var event_is_load_s = false;

            var ad_sp_listener = function(event){
                create_spec_ads(cl);
            }

            var ad_sp_listener_s = function(event){
                create_spec_ads_s(cl);
            }

            var add_events = function(){
                event_is_load = true;
                event_is_load_s = true;
                window.addEventListener('scroll', ad_sp_listener_s);
                window.addEventListener('click', ad_sp_listener);
                window.addEventListener('touchstart', ad_sp_listener);
            }

            var clear_events = function(){
                event_is_load = false;
                window.removeEventListener('click', ad_sp_listener);
                window.removeEventListener('touchstart', ad_sp_listener);
            }

            var clear_event_scroll = function(){
                event_is_load_s = false;
                window.removeEventListener('scroll', ad_sp_listener_s);
            }

            var create_spec_ads_s = function(cl){
                if(event_is_load_s){
                    if(window.pageYOffset > 200){
                        type_load = 2;
                        clear_events();
                        clear_event_scroll();
                        load_srcs(['/user/js/adriver.core.2.js'], function(){
                            create_ads_special(cl, html, script, callback, position);
                        });
                    }
                }
            }

            var create_spec_ads = function(cl){
                if(event_is_load){
                    clear_events();
                    clear_event_scroll();
                }
                type_load = 1;
                load_srcs(['/user/js/adriver.core.2.js'], function(){
                    create_ads_special(cl, html, script, callback, position);
                });
            }

