                    special_banner_load = true;
                }else{
                    create_spec_ads(cl);
                }
            }
            */
                is_refer = 1;
                add_events();
            }else{
                if(window.innerWidth < 641){
                    save_ads_after_load[23] = [cl,html,script,callback];
                    special_banner_load = true;
                }else{
                    create_spec_ads(cl);
                }
            }

            document.querySelector('#check_is_first_load').dataset.type = 0;
        }


