        </style>
        <script>
            if( window.innerWidth >= 769 ){
                document.addEventListener("scroll", scroll);
            }

            function scroll() {
                if(document.querySelectorAll(".sticky").length > 0) {
                    let multimedia = document.querySelector(".multimedia");
                    let sticky = document.querySelector(".sticky").offsetWidth;
                    let multimediaToTop = multimedia.offsetTop + multimedia.scrollHeight;
                    let headerHeight = document.querySelector("header").offsetHeight;

                    const bodyHeight = document.body.offsetHeight;
                    const screenHeight = window.innerHeight;
                    const scrolled = window.scrollY;
                    const userPosition = scrolled + screenHeight;

                    let contentBaseHeight = document.querySelector(".content__base");
                    let contentNewsHeight = document.querySelector(".content__news");
                    let multimediaToTopFix = multimediaToTop - screenHeight;


                    if(userPosition >= multimediaToTop){
                        contentBaseHeight.classList.remove('scroll');

                        contentBaseHeight.style.width = sticky + "px";

                        if(contentBaseHeight.classList.contains('scroll') || contentBaseHeight.classList.contains('scroll-fix')){
                            contentBaseHeight.style.top = "-"+multimediaToTopFix+"px";
                        }

                        contentBaseHeight.classList.add('fixe-scroll');

                    }else if(userPosition <= multimediaToTop){
                        contentBaseHeight.classList.add('scroll');
                    }

                    if(userPosition > contentNewsHeight.offsetHeight + headerHeight && !contentBaseHeight.classList.contains('scroll')){
                        contentBaseHeight.style.top = 0;
                        contentBaseHeight.classList.remove('scroll-fix');
                    }

                    if(scrolled == 0){
                        contentBaseHeight.classList.remove('fixe-scroll');
                        contentBaseHeight.style.top = "-"+multimediaToTopFix+"px";
                    }
                } else {
                    document.removeEventListener("scroll", scroll);
                }
            }
        </script>
